def dispense(n, coins):
    dispensed = []
    while n > 0:
        for i in range(len(coins)):
            curr_coin_amount = coins[i]
            if n >= curr_coin_amount:
                num_coins_used = n // curr_coin_amount
                n -= curr_coin_amount * num_coins_used
                for j in range(num_coins_used):
                    dispensed += [curr_coin_amount]
    return dispensed

